import React, { useContext, useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Context } from "../../context/Context";

export default function Topbar() {
  const { user, dispatch } = useContext(Context);
  const [profilePicUrl, setProfilePicUrl] = useState("");
  const PF = "http://localhost:5000/images/";

  useEffect(() => {
    if (user && user.profilePic) {
      setProfilePicUrl(PF + user.profilePic);
    } else {
      setProfilePicUrl(""); // Reset the profile picture URL if user is not logged in or doesn't have a profile picture
    }
  }, [user]);

  const handleLogout = () => {
    dispatch({ type: "LOGOUT" });
  };

  return (
    <div className="top">
      <div className="topLeft">
        <i className="topicon fa-brands fa-facebook"></i>
        <i className="topicon fa-brands fa-twitter"></i>
        <i className="topicon fa-brands fa-pinterest"></i>
        <i className="topicon fa-brands fa-instagram"></i>
      </div>
      <div className="topcenter">
        <ul className="toplist">
          <li className="toplistItem">
            <Link className="link" to="/">
              HOME
            </Link>
          </li>
          <li className="toplistItem">
            <Link className="link" to="/Destination">
              DESTINATIONS
            </Link>
          </li>
          <li className="toplistItem">
            <Link className="link" to="/ideas">
              IDEAS
            </Link>
          </li>
          <li className="toplistItem">
            <Link className="link" to="/Write">
              WRITE
            </Link>
          </li>
          {user && (
            <li className="topListItem" onClick={handleLogout}>
              LOGOUT
            </li>
          )}
        </ul>
      </div>
      <div className="topRight">
        {user ? (
          <Link className="link" to="/Settings">
            <img className="topimg" src={profilePicUrl} alt="Profile" />
          </Link>
        ) : (
          <ul className="toplist">
            <li className="toplistItem">
              <Link className="link" to="/login">
                LOGIN
              </Link>
            </li>
            <li className="toplistItem">
              <Link className="link" to="/Register">
                REGISTER
              </Link>
            </li>
          </ul>
        )}
        <i className="topSearchIcon fas fa-search"></i>
      </div>
    </div>
  );
}
