import "./topbar.css";
import { useHistory } from "react-router-dom";
import { Context } from "../../context/Context";
import { useContext, useState } from "react";

export default function Topbar() {
  const history = useHistory();
  const { user, dispatch } = useContext(Context);
  const PF = "http://localhost:5000/images/";
  const [profilePic, setProfilePic] = useState(user ? PF + user.profilePic : "");

  const handleLogout = () => {
    dispatch({ type: "LOGOUT" });
  };

  const handleHome = () => {
    history.push("/");
  };

  const handleHotels = () => {
    window.open("https://travelblogging-hotel-booking.netlify.app/", "_blank");
  };

  const handleIdeas = () => {
    history.push("/ideas");
  };

  const handleWrite = () => {
    history.push("/Write");
  };

  const handleLogin = () => {
    history.push("/login");
  };

  const handleRegister = () => {
    history.push("/Register");
  };

  return (
    <>
      <div className="navbar bg-base-100">
        <div className="topLeft">
          <i className="topicon fa-brands fa-facebook"></i>
          <i className="topicon fa-brands fa-twitter"></i>
          <i className="topicon fa-brands fa-pinterest"></i>
          <i className="topicon fa-brands fa-instagram"></i>
        </div>
        <div className="topcenter">
          <ul className="toplist">
            <li className="toplistItem">
              <button className="link" onClick={handleHome}>
                HOME
              </button>
            </li>
            <li className="toplistItem">
              <button className="link" onClick={handleHotels}>
                HOTELS
              </button>
            </li>
            <li className="toplistItem">
              <button className="link" onClick={handleIdeas}>
                IDEAS
              </button>
            </li>
            <li className="toplistItem">
              <button className="link" onClick={handleWrite}>
                WRITE
              </button>
            </li>
            {user && (
              <li className="topListItem">
                <button className="link" onClick={handleLogout}>
                  LOGOUT
                </button>
              </li>
            )}
          </ul>
        </div>
        <div className="topRight">
          {user ? (
            <button className="link" onClick={() => history.push("/Settings")}>
              <img className="topimg" src={profilePic} alt="profile" />
            </button>
          ) : (
            <ul className="toplist">
              <li className="toplistItem">
                <button className="link" onClick={handleLogin}>
                  LOGIN
                </button>
              </li>
              <li className="toplistItem">
                <button className="link" onClick={handleRegister}>
                  REGISTER
                </button>
              </li>
            </ul>
          )}
        </div>
      </div>
    </>
  );
}
