import React, { useState } from 'react';

import { json } from "./json";
import { themeJson } from "./theme";

const Hotel = () => {
  const [formData, setFormData] = useState({});
  
  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };
  
  const handleSubmit = (event) => {
    event.preventDefault();
    // Handle form submission here
    console.log("Form submitted:", formData);
  };
  
  return (
    <div>
      <h2>{json.title}</h2>
      <form onSubmit={handleSubmit}>
        {json.pages.map((page, pageIndex) => (
          <div key={pageIndex}>
            {page.elements.map((element, elementIndex) => {
              switch (element.type) {
                case "text":
                  return (
                    <input
                      key={elementIndex}
                      type={element.inputType}
                      name={element.name}
                      placeholder={element.placeholder}
                      onChange={handleInputChange}
                      value={formData[element.name] || ""}
                    />
                  );
                case "dropdown":
                  return (
                    <select
                      key={elementIndex}
                      name={element.name}
                      onChange={handleInputChange}
                      value={formData[element.name] || ""}
                    >
                      <option value="">{element.placeholder}</option>
                      {element.choices.map((choice, choiceIndex) => (
                        <option key={choiceIndex} value={choice.value || choice}>
                          {choice.text || choice}
                        </option>
                      ))}
                    </select>
                  );
                // Add other cases for different element types as needed
                default:
                  return null;
              }
            })}
          </div>
        ))}
        <button type="submit">{json.completeText}</button>
      </form>
    </div>
  );
};

export default Hotel;
