import React, { useState } from 'react';
import axios from 'axios';

const SearchHotels = () => {
  const [languages, setLanguages] = useState([]);
  const [error, setError] = useState(null);

  const fetchLanguages = async () => {
    const options = {
      method: 'GET',
      url: 'https://booking-com15.p.rapidapi.com/api/v1/meta/getLanguages',
      headers: {
        'X-RapidAPI-Key': 'fabeeec9d5mshe5dccb93c22a067p1a21cbjsn785507b695c1',
        'X-RapidAPI-Host': 'booking-com15.p.rapidapi.com'
      }
    };

    try {
      const response = await axios.request(options);
      setLanguages(response.data);
    } catch (error) {
      setError(error.message);
    }
  };

  // Function to render languages
  const renderLanguages = () => {
    if (error) {
      return <div>Error: {error}</div>;
    } else {
      return (
        <ul>
          {languages.map(language => (
            <li key={language.language_code}>{language.language_name}</li>
          ))}
        </ul>
      );
    }
  };

  return (
    <div>
      <h1>Search Hotels</h1>
      <button onClick={fetchLanguages}>Fetch Languages</button>
      <div>
        <h2>Languages</h2>
        {renderLanguages()}
      </div>
    </div>
  );
};

export default SearchHotels;
