import { Link } from "react-router-dom";
import axios from "axios";
import { useState } from "react";

export default function Register() {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(false);
    try {
      const res = await axios.post("/auth/register", {
        username,
        email,
        password,
      });
      res.data && window.location.replace("/login");
    } catch (err) {
      setError(true);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <form className="p-6 bg-white rounded shadow-md" onSubmit={handleSubmit}>
        <input className="w-full p-2 mb-4 border rounded" type="text" placeholder="Enter your username..." onChange={e=>setUsername(e.target.value)} />
        <input className="w-full p-2 mb-4 border rounded" type="email" placeholder="Enter your email..." onChange={e=>setEmail(e.target.value)} />
        <input className="w-full p-2 mb-4 border rounded" type="password" placeholder="Enter your password..." onChange={e=>setPassword(e.target.value)} />
        <button className="w-full p-2 mb-4 text-white bg-blue-500 rounded hover:bg-blue-600" type="submit">Register</button>
      </form>
      <button className="p-2 text-white bg-green-500 rounded hover:bg-green-600">
        <Link className="link" to="/login">LOGIN</Link>
      </button>
      {error && <span className="mt-4 text-red-500">Something went wrong</span>}
    </div>
  );
}
